(50, 'PASILLA SUITE ', '<h1><span style="font-size:13px; line-height:1.2em">THE SAME AS A MIRASOL SUITE BUT WITH A RESTRICTED VIEW.</span></h1>\r\n\r\n<ul>\r\n	<li>Sizeable bedroom with super king size bed and sensual contour body forming support mattress with luxury linen.</li>\r\n	<li>Dressing room with dressing table, hairdryer, fitted wardrobe and free safety deposit box.</li>\r\n	<li>Four piece bathroom suite with branded Spice towels.</li>\r\n	<li>Private balcony/terrace with table and chairs.</li>\r\n	<li>Refrigerator stocked daily with a selection of soft drinks.</li>\r\n	<li>Tea and coffee making facilities.</li>\r\n	<li>TV with adult channel.</li>\r\n	<li>CD/MP3 Player.</li>\r\n	<li>Bathrobes, slippers and basic toiletries.</li>\r\n	<li>Daily housekeeping service.</li>\r\n	<li>Free Wifi.</li>\r\n	<li>All-inclusive board basis.</li>\r\n</ul>\r\n', 35, NULL, 'cascabel3-521de166438e60.jpg', 'pasilla-suite', NULL, 0),
(51, 'MIRASOL SUITE ', '<h1><span style="font-size:13px; line-height:1.6em">Sizeable bedroom with super king size bed and sensual contour body forming support mattress with luxury linen.</span></h1>\r\n\r\n<ul>\r\n	<li>Dressing room with dressing table, hairdryer, fitted wardrobe and free safety deposit box.</li>\r\n	<li>Four piece bathroom suite with branded Spice towels.</li>\r\n	<li>Private balcony/terrace with table and chairs.</li>\r\n	<li>Refrigerator stocked daily with a selection of soft drinks.</li>\r\n	<li>Tea and coffee making facilities.</li>\r\n	<li>TV with adult channel.</li>\r\n	<li>CD/MP3 Player.</li>\r\n	<li>Bathrobes, slippers and basic toiletries.</li>\r\n	<li>Daily housekeeping service.</li>\r\n	<li>Free Wifi.</li>\r\n	<li>All-inclusive board basis.</li>\r\n</ul>\r\n', 35, NULL, 'mirasol1-521de188b9da60.jpg', 'mirasol-suite', NULL, 0),
(52, 'CASCABEL SUITE', '<ul>\r\n	<li>Sizeable bedroom with super king size bed and sensual contour body forming support mattress with luxury linen.</li>\r\n	<li>Dressing room with dressing table, hairdryer, fitted wardrobe and free safety deposit box.</li>\r\n	<li>Refurbished four piece shower room with branded Spice towels.</li>\r\n	<li>Private balcony/terrace with table and chairs.</li>\r\n	<li>Refrigerator stocked daily with a selection of soft drinks, wine, beer &amp; snacks.</li>\r\n	<li>Tea and coffee making facilities.</li>\r\n	<li>Flatscreen TV with adult channel.</li>\r\n	<li>IPod docking station.</li>\r\n	<li>Bathrobes, slippers and luxury toiletries.</li>\r\n	<li>Daily housekeeping service.</li>\r\n	<li>Air conditioning.</li>\r\n	<li>Modern furnishings.</li>\r\n	<li>Free Wifi.</li>\r\n	<li>All-inclusive board basis.</li>\r\n</ul>\r\n', 35, NULL, 'mirasol1-521de188b9da61.jpg', 'cascabel-suite', NULL, 0),
(53, '4* Petinos, 4* Naros, 4* Ios Palace, 5* Daedalus ', '<strong><span style="color:rgb(0, 0, 255)">HERE ARE THE FACTS:</span></strong>&nbsp;<u>Included</u> in your 9 night, 10 day package are the following:&nbsp;<br />\r\n- Hotel Rooms with Queen Beds. Close to all Amenities and Sight seeing.&nbsp;<br />\r\n- Arrival into Athen&#39;s Airport and take Connecting flight to&nbsp;<u>Mykonos Island.</u>&nbsp;<strong>June 18,19,20</strong><br />\r\n- Transfers from Mykonos Airport to&nbsp;<span style="color:rgb(255, 0, 0)"><strong>4* Petinos Hotel</strong></span>, Breakfast included daily for your 3 night stay.&nbsp;<br />\r\n- Transfers to Port and board Ferry over to&nbsp;<u>island of Paros&nbsp;</u>where you will be transfered to&nbsp;<span style="color:rgb(255, 0, 0)"><strong>4* Naros of Paros Hotel</strong></span>, Breakfast Indluded&nbsp;<strong>June 21,22</strong><br />\r\n- Transfer to Port and board Ferry to&nbsp;<u>island of Ios</u>&nbsp;where you will be transferred to&nbsp;<span style="color:rgb(255, 0, 0)"><strong>4* Ios Palace Hotel,</strong></span>&nbsp;Breakfast Included&nbsp;<strong>June 23,24</strong><br />\r\n- Transfer to Port and board Ferry to&nbsp;<u>Island of Santorini</u>&nbsp;where you will be transferred to&nbsp;<strong><span style="color:rgb(255, 0, 0)">5* Daedalus,</span></strong>&nbsp;<strong>June 25,26</strong>&nbsp;<br />\r\n<br />\r\nRates include transfers and accommodations, breakfast daily, inter-island ferries, hotel taxes, porterage, local assistance, service charges &amp; entrance fees per itinerary. NOTE: Flights from Athen&#39;s to Mykonos are approx 80 Euros each and will be booked at time of reservation. Price will be subject to time of booking. Same for Santorini back to Athen&#39;s. We will arrange this on your behalf.&nbsp;', 36, NULL, 'greece0.jpg', '4-petinos-4-naros-4-ios-palace-5-daedalus', NULL, 0),
(54, 'STANDARD ROOM', '<div class="main-wrapper container" style="margin: 0px auto; padding: 0px 15px; max-width: 1008px; color: rgb(102, 102, 102); font-family: Arial; font-size: 12px; line-height: 22px;">\r\n<div class="row row-wrapper" style="width: 1038px; max-width: 100%; margin: 0px auto;">\r\n<div class="page-outer-wrapper" style="min-height: 500px;">\r\n<div class="page-wrapper twelve columns left-sidebar b0" style="margin-bottom: 0px !important; float: left; min-height: 1px; padding: 0px 15px; position: relative; box-sizing: border-box; width: 1008px;">\r\n<div class="row" style="width: auto; max-width: none; margin: 0px -15px; min-width: 0px;">\r\n<div class="content-wrapper eight columns b0" style="margin-bottom: 0px !important; float: left; min-height: 1px; padding: 0px 15px; position: relative; box-sizing: border-box; width: 672px;">\r\n<div class="page-content">\r\n<div class="text-content">\r\n<p>Relax in total comfort in these well-appointed rooms surrounding our garden area. Decorated in subtle shades of beige with aqua or peach, our rooms are elegantly furnished in bleached wood. You&rsquo;ll also find everything you need to do business and then relax. Choice of king or twin beds. Every room has a patio or balcony with a sea view.</p>\r\n\r\n<h3>Room Features</h3>\r\n\r\n<div class="four columns b0" style="margin-bottom: 0px !important; float: left; min-height: 1px; padding: 0px 15px; position: relative; box-sizing: border-box; width: 213px;">\r\n<ul>\r\n	<li>Accommodate max. 3 Persons</li>\r\n	<li>Located ground floor or 2nd floor</li>\r\n	<li>Patio or Balcony</li>\r\n	<li>King Bed or 2 Double beds</li>\r\n	<li>Garden &amp; Sea View</li>\r\n</ul>\r\n</div>\r\n\r\n<div class="four columns b0" style="margin-bottom: 0px !important; float: left; min-height: 1px; padding: 0px 15px; position: relative; box-sizing: border-box; width: 213px;">\r\n<ul>\r\n	<li>Bathroom with hairdryer</li>\r\n	<li>Air-conditioned</li>\r\n	<li>Satellite TV.</li>\r\n	<li>Mini-safe, Telephone, Wireless Internet (Additional Cost).</li>\r\n	<li>Iron + Ironing board</li>\r\n</ul>\r\n</div>\r\n\r\n<div class="four columns b0" style="margin-bottom: 0px !important; float: left; min-height: 1px; padding: 0px 15px; position: relative; box-sizing: border-box; width: 213px;">\r\n<ul>\r\n	<li>Coffeemaker</li>\r\n	<li>Mini Fridge &ndash; empty</li>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n\r\n<div class="page-single-element">\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class="right-sidebar-wrapper four columns" style="float: left; min-height: 1px; padding: 0px 15px 0px 30px; position: relative; margin-bottom: 30px; box-sizing: border-box; width: 335px;">\r\n<div class="right-sidebar sidebar">\r\n<div class="widget-container widget_stupcommingevents" id="stupcommingevents-2" style="margin-bottom: 30px;">\r\n<h3>Upcoming Events</h3>\r\n</div>\r\n\r\n<div class="widget-container widget_sttwitter" id="sttwitter-2" style="margin-bottom: 30px;">\r\n<h3>Latest Tweets</h3>\r\n\r\n<div class="twitter-update">\r\n<ul>\r\n</ul>\r\n</div>\r\n</div>\r\n\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n</div>\r\n\r\n<div class="clear" style="clear: both; height: 0px;">&nbsp;</div>\r\n</div>\r\n\r\n<div class="subscribe_section" style="padding: 40px 30px; background-color: rgb(245, 245, 245); margin: 0px auto; text-align: center; color: rgb(102, 102, 102); font-family: Arial; font-size: 12px; line-height: 22px;">&nbsp;</div>\r\n', 37, NULL, 'standard_room_ca0.jpg', 'standard-room-1', NULL, 0),
